@app.route('/logout')
def logout():
    session.pop('logged_in', None)
    return redirect('/login')

@app.route('/home', methods=['GET', 'POST'])
def home():
    if not session.get('logged_in'):
        return redirect('/login')

    # ঠিক করা হয়েছে: area_filter → area
    area = request.args.get('area', 'all')

    # সার্চ
    search_query = request.form.get('search', '').strip().lower() if request.method == 'POST' else ''

    filtered = companies
    if area != 'all':
        filtered = [c for c in filtered if c['area'] == area]
    if search_query:
        filtered = [c for c in filtered if 
                    search_query in c['name'].lower() or 
                    search_query in c['location'].lower() or 
                    search_query in c['id'] or
                    search_query in (c.get('mar', '') or '').lower()]

    return render_template('index.html',  # তোমার HTML ফাইলের নাম যদি index.html হয়
                           companies=filtered,
                           selected_area=area,  # এখানে area_filter না, area
                           total=len(companies),
                           my_name=MY_NAME,
                           my_photo=MY_PHOTO,
                           my_logo=MY_LOGO,
                           my_whatsapp=MY_WHATSAPP,
                           my_location=MY_LOCATION)

@app.route('/')
def index():
    return redirect('/login')

